# AI assisted development# Frontend Deployment Script for MedExJob.com# This script builds the frontend and uploads to VPSparam(    [string]$VPS_IP = "72.62.196.181",    [string]$VPS_USER = "root",    [string]$VPS_PATH = "/var/www/medexjob")Write-Host "========================================" -ForegroundColor CyanWrite-Host "MedExJob Frontend Deployment Script" -ForegroundColor CyanWrite-Host "========================================" -ForegroundColor CyanWrite-Host ""# Step 1: Navigate to frontend directoryWrite-Host "[1/5] Navigating to frontend directory..." -ForegroundColor Yellow$scriptPath = Split-Path -Parent $MyInvocation.MyCommand.Path$projectRoot = Split-Path -Parent $scriptPath$frontendPath = Join-Path $projectRoot "frontend"if (-not (Test-Path $frontendPath)) {    Write-Host "ERROR: Frontend directory not found at $frontendPath" -ForegroundColor Red    exit 1}Set-Location $frontendPathWrite-Host "Ã¢Å“â€œ Current directory: $frontendPath" -ForegroundColor GreenWrite-Host ""# Step 2: Build frontendWrite-Host "[2/5] Building frontend..." -ForegroundColor Yellownpm run buildif ($LASTEXITCODE -ne 0) {    Write-Host "ERROR: Build failed!" -ForegroundColor Red    exit 1}Write-Host "Ã¢Å“â€œ Build completed successfully" -ForegroundColor GreenWrite-Host ""# Step 3: Check if dist folder exists$distPath = Join-Path $frontendPath "dist"if (-not (Test-Path $distPath)) {    Write-Host "ERROR: dist folder not found!" -ForegroundColor Red    exit 1}Write-Host "[3/5] Files ready for upload:" -ForegroundColor YellowGet-ChildItem $distPath -Recurse | Measure-Object -Property Length -Sum | ForEach-Object {    $sizeMB = [math]::Round($_.Sum / 1MB, 2)    Write-Host "  Total size: $sizeMB MB" -ForegroundColor Cyan}Write-Host ""# Step 4: Upload to VPSWrite-Host "[4/5] Uploading files to VPS..." -ForegroundColor Yellow$vpsConnection = $VPS_USER + "@" + $VPS_IP$vpsFullPath = $vpsConnection + ":" + $VPS_PATHWrite-Host "  VPS: $vpsFullPath" -ForegroundColor Cyan# Check if SCP is available$scpPath = Get-Command scp -ErrorAction SilentlyContinueif (-not $scpPath) {    Write-Host "ERROR: SCP not found. Please install OpenSSH or use WinSCP manually." -ForegroundColor Red    Write-Host "  Manual upload: Copy files from $distPath to $VPS_PATH on VPS" -ForegroundColor Yellow    exit 1}# Upload filesWrite-Host "  Uploading files (this may take a moment)..." -ForegroundColor Cyan$scpDestination = $vpsConnection + ":" + $VPS_PATH + "/"scp -r "$distPath\*" $scpDestinationif ($LASTEXITCODE -ne 0) {    Write-Host "ERROR: Upload failed! Please check:" -ForegroundColor Red    Write-Host "  1. SSH connection to VPS" -ForegroundColor Yellow    Write-Host "  2. VPS credentials" -ForegroundColor Yellow    Write-Host "  3. Use WinSCP for manual upload" -ForegroundColor Yellow    Write-Host ""    Write-Host "Manual upload steps:" -ForegroundColor Yellow    Write-Host "  WinSCP: $distPath -> $vpsFullPath" -ForegroundColor Cyan    exit 1}Write-Host "Ã¢Å“â€œ Files uploaded successfully" -ForegroundColor GreenWrite-Host ""# Step 5: Set permissions and reload Nginx (via SSH)Write-Host "[5/5] Setting permissions and reloading Nginx..." -ForegroundColor YellowWrite-Host "  Connecting to VPS..." -ForegroundColor Cyan$sshTarget = $vpsConnection$cmd1 = "cd " + $VPS_PATH$cmd2 = "chown -R www-data:www-data ."$cmd3 = "chmod -R 755 ."$cmd4 = "chmod 644 index.html"$cmd5 = "nginx -t"$cmd6 = "systemctl reload nginx"$fullCommand = ($cmd1, $cmd2, $cmd3, $cmd4, $cmd5, $cmd6) -join "; "ssh $sshTarget $fullCommandif ($LASTEXITCODE -ne 0) {    Write-Host 'WARNING: Permission/Nginx commands may have failed. Please run manually on VPS:' -ForegroundColor Yellow    Write-Host ('  cd ' + $VPS_PATH) -ForegroundColor Cyan    Write-Host '  chown -R www-data:www-data .' -ForegroundColor Cyan    Write-Host '  chmod -R 755 .' -ForegroundColor Cyan    Write-Host '  chmod 644 index.html' -ForegroundColor Cyan    Write-Host '  nginx -t' -ForegroundColor Cyan    Write-Host '  systemctl reload nginx' -ForegroundColor Cyan} else {    Write-Host 'Permissions set and Nginx reloaded' -ForegroundColor Green}Write-Host ""Write-Host "========================================" -ForegroundColor CyanWrite-Host "Deployment Complete!" -ForegroundColor GreenWrite-Host "========================================" -ForegroundColor CyanWrite-Host ""Write-Host 'Next steps:' -ForegroundColor YellowWrite-Host '  1. Test website: https://medexjob.com' -ForegroundColor CyanWrite-Host '  2. Hard refresh: Ctrl + Shift + R' -ForegroundColor CyanWrite-Host ""